mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT
FROM cms_comment a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
