mapper.select:
SELECT 

FROM cms_daily_list_content a

WHERE a.id = #{id}

SELECT 

FROM cms_daily_list_content a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_daily_list_content a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_daily_list_content SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
