mapper.select:
SELECT
FROM account_attention a left join sys_user s
WHERE a.id = #{id}

SELECT
FROM account_attention a left join sys_user s1 on s1.id=a.from_user left join sys_user s2 on s2.id=a.to_user
a.del_flag = #{DEL_FLAG_NORMAL}
and s1.login_name like concat(concat('%',#{fromUserName}),'%')
ORDER BY ${page.orderBy}

SELECT 

FROM account_attention a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE account_attention SET 	
	from_user = #{fromUser},
	to_user = #{toUser}
WHERE id = #{id}

UPDATE account_attention SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
