mapper.select:
SELECT 

FROM cms_activity a

WHERE a.id = #{id}

SELECT 

FROM cms_activity a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.start_date = #{startDate}


		AND a.end_date = #{endDate}


		AND a.enter_start_date = #{enterStartDate}


		AND a.enter_end_date = #{enterEndDate}


		AND a.create_date = #{createDate}


		AND a.update_date = #{updateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_activity a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_activity SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
