mapper.select:
SELECT 

FROM cms_activity_flow a

WHERE a.id = #{id}

SELECT 

FROM cms_activity_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.flow_start_time = #{flowStartTime}


		AND a.flow_end_time = #{flowEndTime}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}

SELECT 

FROM cms_activity_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_activity_flow SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
