mapper.select:
SELECT 
	
FROM cms_banner a

WHERE a.id = #{id}

SELECT 
	
FROM cms_banner a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')
			
	
	
		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
	


	
		ORDER BY ${page.orderBy}

SELECT 
	
FROM cms_banner a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_banner SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
