mapper.select:
SELECT 

FROM cms_complaint a

WHERE a.id = #{id}

SELECT 

FROM cms_complaint a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.status in('0','1','2','3')

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.company_name = #{companyName}


		AND a.user_id = #{userId}




		ORDER BY ${page.orderBy}

SELECT 

FROM cms_complaint a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_complaint SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
