mapper.select:
SELECT 

FROM cms_news_data a

WHERE a.id = #{id}

SELECT 
	a.id ,a.news_id,a.content,a.content_html,a.del_flag,b.id  titleid, b.title
		from cms_news b LEFT JOIN  (select * from cms_news_data where del_flag !='1') a on a.news_id=b.id



	b.del_flag='0' 

		AND a.id = #{id}


		AND a.news_id = #{newsId}


		AND a.content = #{content}


		AND a.content_html = #{contentHtml}





		ORDER BY ${page.orderBy}

SELECT 

FROM cms_news_data a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_news_data SET 	
	news_id = #{newsId},
	content = #{content},
	content_html = #{contentHtml},
	del_flag = #{delFlag}
WHERE id = #{id}

UPDATE cms_news_data SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
