mapper.select:
SELECT 
	
FROM cms_product a

WHERE a.id = #{id}

SELECT 
	
FROM cms_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')
	
	
		AND a.tags = #{tags}
	
	
		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC,a.recommend

SELECT 
	
FROM cms_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC,a.recommend

mapper.update:
UPDATE cms_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
