mapper.select:
SELECT 

FROM cms_share a

WHERE a.id = #{id}

SELECT
FROM cms_share a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM cms_share a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_share SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
