mapper.select:
SELECT T.JGDM AS JYS,T.JGMC AS JYSMC,H.JYSINFO,T.FXLB AS FXDL,T.FXZB ,T.FXZBZ,T.YUZHI AS YZ,T.CCE,SUBSTRING(T.BJSJ,1,8) AS YJSJ,T.CLZT FROM HUB_FXSJ T LEFT JOIN HUB_REF_JYSINFO H ON T.JGDM=H.JYS WHERE T.JYSFL=#{riskType}
and T.FXLB = #{riskName}
and T.JGDM = #{jys}
and T.JGDM IN ('0012','0014','0015') ORDER BY T.BJSJ DESC
