mapper.select:
SELECT 

FROM account_subscribe_history a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{userId}




		ORDER BY ${page.orderBy}

SELECT 

FROM account_subscribe_history a

WHERE a.id = #{id}

SELECT 

FROM account_subscribe_history a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE account_subscribe_history SET 	
	user_id = #{user.id},
	lab_id = #{labId}
WHERE id = #{id}

UPDATE account_subscribe_history SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
