mapper.select:
SELECT 

FROM account_thirdplat a

WHERE a.id = #{id}

SELECT 

FROM account_thirdplat a

WHERE a.platkey = #{platkey} and a.ptype=#{ptype}

SELECT
FROM account_thirdplat a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_id = #{user.id}
AND a.ptype = #{ptype}
AND a.platkey = #{platkey}
AND a.nick_name LIKE '%'||#{nickName}||'%' '%'+#{nickName}+'%' concat('%',#{nickName},'%')
AND a.province LIKE '%'||#{province}||'%' '%'+#{province}+'%' concat('%',#{province},'%')
AND a.city LIKE '%'||#{city}||'%' '%'+#{city}+'%' concat('%',#{city},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.country LIKE '%'||#{country}||'%' '%'+#{country}+'%' concat('%',#{country},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM account_thirdplat a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE account_thirdplat SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
