mapper.select:
SELECT
FROM 1169_thinker_api_errorcode a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.api_id = #{apiId}
AND a.error_code LIKE '%'||#{errorCode}||'%' '%'+#{errorCode}+'%' concat('%',#{errorCode},'%')
AND a.descs LIKE '%'||#{descs}||'%' '%'+#{descs}+'%' concat('%',#{descs},'%')
AND a.deal_method LIKE '%'||#{dealMethod}||'%' '%'+#{dealMethod}+'%' concat('%',#{dealMethod},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM 1169_thinker_api_errorcode a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE 1169_thinker_api_errorcode SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE api_id = #{apiId}
