mapper.select:
SELECT 

FROM 1169_thinker_api_success a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.api_id = #{apiId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM 1169_thinker_api_success a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE 1169_thinker_api_success SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE api_id = #{apiId}
