mapper.select:
SELECT 

FROM 1169_thinker_hot_search a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.keyword LIKE 
			'%'||#{keyword}||'%'
			'%'+#{keyword}+'%'
			concat('%',#{keyword},'%')


		AND a.nums >= #{nums}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

